function startPage() {
    var $main = $("#main-jumbotron");
    var result = '' + '<p class="jumbo-info">' + '<div class="progress">' + '<div id="loading-page" class="progress-bar progress-bar-striped active" role="progressbar" aria-valuenow="100" aria-valuemin="0" aria-valuemax="100" style="width:100%">' + 'Načítavam, prosím čakajte' + '</div>' + '</div>' + '</p>';
    $main.html(result);
    $(window).resize(function () {
        resizeWindow();
    });
    sendPostRequest({ action: "get_events" }).then(function (result) {
        successfulStart(result, true);
    });
}

function successfulStart(result, start) {
    result = JSON.parse(result);
    if (result.status.code !== 11) toLogin();else {
        if (result["level"]) LEVEL = parseInt(result["level"]);
        var res = parseResponse(result.data);
        events = new Events();
        res.forEach(function (e) {
            events.add(new Event(e));
        });
        if (start) {
            // setInterval(function () {ping();}, 300000);
            setInterval(function () {ping();}, 300000);
            var $el = $('#main-datepicker');
            $el.datetimepicker({
                inline: true,
                keepOpen: true,
                format: 'LL',
                locale: 'sk'
            });
            $el.on("dp.change", function (e) {
                mainDateChanged(e.date._d.toString());
            });
            invokeCalendarClick();
        } else {
            invokeCalendarClick();
            viewChanged(viewing_mode, viewing_params);
        }
        resizeMain();
    }
}

function resizeWindow() {
    $("body").height(window.innerHeight);
    resizeMain();
}

function ping() {
    sendPostRequest({ action: "ping" }).then(function (result) {
        // var res = JSON.parse(result);
        // console.log("pinged", JSON.parse(result));
        if (result) successfulStart(result);
    });
}

function resizeMain() {
    setTimeout(function () {
        var h = 180 + $("#main-jumbotron").height();
        if (h > window.innerHeight) $("#main").height(h);
    }, 600);
}